package testJava_eight_array;

public class Test_8_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;
		int a[];
		a=new int[3];
		for(i=0;i<3;i++){
			System.out.print("a["+i+"]="+a[i]+",\t");//a["+i+"]
		}
		System.out.println("\n 数组长度是："+a.length);
	}
}
